for (int i=0; i < 2; ++i) {
  real a=(i == 0) ? 60 : -100;
  /*<asyxml><view file="modules/geometry.asy" type="point" signature="arcsubtendedcenter(point,point,real)"/></asyxml>*/
  dot(arcsubtendedcenter(A,B,a),2mm+blue);

