    if(i != nm) p += na+d+dl*t;
  }
  add(Sh*pic);
  pair M=(p.x,0);
  picture pic2=reflect(M,M+N)*pic;
  add(Sh*pic2);
  add(Sh*reflect(3*M,3*M+N)*pic2);
}

shipout(bbox(1mm,Fill(lightgrey)));
